function clickImage(e){
			
		var imageic = e.src;
		var imagealt = e.alt;
		
		swal({
		  title: imagealt,
		  imageUrl: imageic,
		  imageWidth: 600,
		  imageHeight: 280,
		  imageAlt: 'Custom image',
		  animation: false
		})
		}

